// insert-start
import app from '@adonisjs/core/services/app'
// insert-end

test('get all users', async ({ client }) => {
  // insert-start
  class FakeService extends UserService {
    all() {
      return [{ id: 1, username: 'virk' }]
    }
  }

  app.container.swap(UserService, () => {
    return new FakeService()
  })
