object BasicPersistentBehaviorCompileOnly {
  trait Command
  trait Event
  trait State

  val behavior: EventSourcedBehavior[Command, Event, State] =
    EventSourcedBehavior.withEnforcedReplies[
      Command,
      Event,
      State](persistenceId = PersistenceId.ofUniqueId("abc"),
        emptyState = null.asInstanceOf[State],
        commandHandler = (state, cmd) => Effect.none.thenNoReply(),
        eventHandler = (state, evt) => null.asInstanceOf[State])
}
