LANGUAGE: Scala
CODE:
```
case object Initialize
case class InitializeMsg(data: Any)
class InitializeViaMsgActor extends Actor {
  override def receive: Receive = {
    case Initialize =>
      context.become(initialized(None))
    case InitializeMsg(data) =>
      context.become(initialized(Some(data)))
  }
  def initialized(state: Option[Any]): Receive = {
    case "get" => sender() ! state
  }
}
