class MyReadJournalProvider(system: ExtendedActorSystem, config: Config) extends ReadJournalProvider {
  override def getReadJournal(): scaladsl.ReadJournal with scaladsl.EventsByTagQuery = 
    new MyScaladslReadJournal(system, config)

  override def getJavadslReadJournal(): javadsl.ReadJournal with javadsl.EventsByTagQuery =
    new MyJavadslReadJournal(system, config)
}

class MyScaladslReadJournal(system: ExtendedActorSystem, config: Config)
  extends scaladsl.ReadJournal with scaladsl.EventsByTagQuery {

  private val refreshInterval: FiniteDuration =
    config.getDuration("refresh-interval", TimeUnit.MILLISECONDS).millis

  override def eventsByTag(tag: String, offset: Offset): Source[EventEnvelope, NotUsed] =
    Source.fromGraph(new MyEventsByTagSource(tag, offset))
}

class MyJavadslReadJournal(system: ExtendedActorSystem, config: Config)
  extends javadsl.ReadJournal with javadsl.EventsByTagQuery {

  private val scalaReadJournal = new MyScaladslReadJournal(system, config)

  override def eventsByTag(tag: String, offset: Offset): javadsl.Source[EventEnvelope, NotUsed] =
    scalaReadJournal.eventsByTag(tag, offset).asJava
}
```

