final case class AuctionState(
  isOpen: Boolean,
  highestBid: Bid,
  highestCounterOffer: Int
) {
  def applyEvent(event: Event): AuctionState = {
    event match {
      case BidRegistered(offer, timestamp, fromReplica) =>
        val newBid = Bid(offer, timestamp, fromReplica)
        if (newBid.isHigherThan(highestBid)) {
          copy(highestBid = newBid, highestCounterOffer = highestBid.offer)
        } else {
          copy(highestCounterOffer = math.max(highestCounterOffer, offer))
        }
      case AuctionFinished(_, _) =>
        copy(isOpen = false)
    }
  }
}
```

