class EchoHandler extends AbstractActor {
  private final ActorRef connection;
  private final LoggingAdapter log = Logging.getLogger(getContext().system(), this);

  public EchoHandler(ActorRef connection) {
    this.connection = connection;
    // sign death pact: this actor terminates when connection breaks
    getContext().watch(connection);
  }

  @Override
  public Receive createReceive() {
    return receiveBuilder().match(Tcp.Received.class, msg -> {
      final ByteString data = msg.data();
      connection.tell(TcpMessage.write(data, Ack.getInstance()), getSelf());
      getContext().become(buffering(new ArrayList<ByteString>()));
    }).match(Tcp.ConnectionClosed.class, msg -> {
      getContext().stop(getSelf());
    }).build();
  }
