  case evt: Evt                               => updateState(evt)
  case SnapshotOffer(metadata, snapshot: State) => state = snapshot
  case RecoveryCompleted =>
    log.info(
      "MyPersistentActor [{}] recovery completed. Current state: [{}]",
      persistenceId,
      state)
}

def receiveCommand: Receive = {
  case GetState => sender() ! state
  case "snap"   => saveSnapshot(state)
  case "print" =>
    log.info(
      "MyPersistentActor [{}] received [print] command. Current state: [{}]",
      persistenceId,
      state)
    if (recoveryRunning) {
      log.info("RECOVERY RUNNING: " + recoveryRunningNum)
    } else {
      log.info("RECOVERY COMPLETED")
    }

  case SaveUser(user) =>
    persist(UserSaved(user)) { evt =>
      updateState(evt)
    }
}
