class ScanningBusImpl extends
  EventBus[String, String, ActorRef] with ScanningClassification {
  // will be invoked for each event for all subscribers
  override protected def publish(event: String, subscriber: ActorRef): Unit = {
    subscriber ! event
  }

  // is needed for determining matching classifiers and subscribers
  override protected def matches(classifier: String, event: String): Boolean = {
    // publish String messages to subscribers that handle events starting with
    // the given classifier
    event.startsWith(classifier)
  }
}
