  override val shape: SourceShape[EventEnvelope] = SourceShape(out)

  override def createLogic(inheritedAttributes: Attributes): GraphStageLogic = new GraphStageLogic(shape) {
    // this query would be completed after processing the events that matched the tag that existed at the time the query was started.
    // A Later query can include events that were stored after the original query was completed.

    var completeAfterIdle: Option[Cancellable] = None

    override def preStart(): Unit = {
      completeAfterIdle = scheduleWithFixedDelay(1.second, 1.second, () => {
        if (isAvailable(out))
          getStoreEvents().foreach(push(out, _))
      })
