final int queueSize = 10;
BoundedSourceQueue<Integer> queue =
    Source.<Integer>queue(queueSize)
        .map(x -> x * x)
        .to(Sink.foreach(x -> System.out.println("completed " + x)))
        .run(system);

QueueOfferResult result = queue.offer(1);
if (result == QueueOfferResult.enqueued()) {
  System.out.println("enqueued");
} else if (result == QueueOfferResult.dropped()) {
  System.out.println("dropped");
} else if (result instanceof QueueOfferResult.Failure) {
  System.out.println("Offer failed: " + ((QueueOfferResult.Failure) result).cause());
} else if (result == QueueOfferResult.QueueClosed()) {
  System.out.println("queue closed");
}
