private def idle(): Behavior[Event] =
  Behaviors.receive { (context, message) =>
    message match {
      case SetTarget(ref) =>
        target = Some(ref)
        Behaviors.same
      case Queue(obj) =>
        queue :+= obj
        active()
      case Flush =>
        Behaviors.unhandled
    }
  }
