// Message protocol for Ack
sealed trait Command
case class Element(value: String, replyTo: ActorRef[Ack]) extends Command
case object Init extends Command
case object Complete extends Command
case class Fail(ex: Throwable) extends Command
case object Ack

val actor: ActorRef[Command] = ??? // Initialized elsewhere

