final class MyEventsByTagSource extends GraphStage<SourceShape<EventEnvelope>> {
  private final String tag;
  private final Offset offset;
  private final Outlet<EventEnvelope> out = Outlet.create("MyEventsByTagSource");
  private final SourceShape<EventEnvelope> shape = SourceShape.of(out);

  public MyEventsByTagSource(String tag, Offset offset) {
    this.tag = tag;
    this.offset = offset;
  }

  @Override
  public SourceShape<EventEnvelope> shape() {
    return shape;
  }

  @Override
  public GraphStageLogic createLogic(Attributes inheritedAttributes) {
    return new GraphStageLogic(shape) {
      private Optional<Cancellable> completeAfterIdle = Optional.empty();

      @Override
      public void preStart() {
        completeAfterIdle =
            Optional.of(
                scheduleWithFixedDelay(
                    Duration.ofSeconds(1),
                    Duration.ofSeconds(1),
                    new Runnable() {
                      @Override
                      public void run() {
                        if (isAvailable(out())) {
                          for (EventEnvelope envelope : getStoreEvents()) {
                            push(out(), envelope);
                          }
                        }
                      }
                    }));
      }

      private List<EventEnvelope> getStoreEvents() {
        // retrieve events from the data store
        // query events by tag, offset
        return Collections.emptyList();
      }

      @Override
      public void postStop() {
        completeAfterIdle.ifPresent(Cancellable::cancel);
      }

      {
        setHandler(
            out,
            new AbstractOutHandler() {
              @Override
              public void onPull() {
