    public static class Limiter extends AbstractActor {
        private final float rate;
        private final Duration per;
        private final int maximumBurst;
        private int tokens;
        private Instant lastReplenish;
        private final Queue<ActorRef> queue;

        public Limiter(float rate, Duration per, int maximumBurst) {
            this.rate = rate;
            this.per = per;
            this.maximumBurst = maximumBurst;
            this.tokens = maximumBurst;
            this.lastReplenish = Instant.now();
            this.queue = new LinkedList<>();

            getContext()
                    .getSystem()
                    .scheduler()
                    .scheduleAtFixedRate(
                            per,
                            per,
                            getSelf(),
                            ReplenishTokens,
                            getContext().getDispatcher(),
                            getSelf());
        }

        @Override
        public Receive createReceive() {
            return receiveBuilder().match(Object.class, this::onMessage).build();
        }

        private void onMessage(Object message) {
            if (message == WantToPass) {
                if (tokens > 0) {
                    tokens--;
                    getSender().tell(MayPass, getSelf());
                } else {
                    queue.offer(getSender());
                }
            } else if (message == ReplenishTokens) {
                replenish();
                while (tokens > 0 && !queue.isEmpty()) {
                    queue.poll().tell(MayPass, getSelf());
                    tokens--;
                }
            }
        }

        private void replenish() {
            Instant now = Instant.now();
            long sinceLastReplenish = Duration.between(lastReplenish, now).toMillis();
            int toAdd = (int) ((sinceLastReplenish / (float) per.toMillis()) * rate);
            tokens = Math.min(tokens + toAdd, maximumBurst);
            lastReplenish = now;
        }
    }
}
```

