class Listener extends Actor {
  IO(Udp) ! Bind(self, new InetSocketAddress("localhost", 0))
  def receive = {
    case Bound(local) =>
      context.become(ready(sender()))
  }
  def ready(socket: ActorRef): Receive = {
    case Received(data, remote) =>
