class RuntimePluginActor extends AbstractPersistentActor {
  private final String id;

  public RuntimePluginActor(String id) {
    this.id = id;
  }

  @Override
  public String persistenceId() {
    return id;
  }

  @Override
  public Config journalPluginConfig() {
    return ConfigFactory.parseString(
        "dir = \"target/journal-runtime-conf\""
            + "\nnative = off")
        .withFallback(context().system().settings().config().getConfig(journalPluginId()));
  }

  @Override
  public Config snapshotPluginConfig() {
    return ConfigFactory.parseString(
        "dir = \"target/snapshot-runtime-conf\"")
        .withFallback(context().system().settings().config().getConfig(snapshotPluginId()));
  }

  // ... rest of the actor implementation ...

  @Override
  public Receive createReceiveRecover() {
    return receiveBuilder().build();
  }

  @Override
  public Receive createReceive() {
    return receiveBuilder().build();
  }
}
