              final ActorRef<String> child1 =
                  context.spawn(behavior, "child1");
              final ActorRef<String> child2 =
                  context.spawn(behavior, "child2");

              return Behaviors.receiveMessage(
                  message -> {
                    // processing messages
                    // ...
                    // restart occurs, children will be stopped
                    return Behaviors.same();
                  });
            }))
