class UserEventAdapter extends EventAdapter {
  override def fromJournal(event: Any, manifest: String): EventSeq = event match {
    case UserDetailsChanged(name, address) =>
      val events = Vector.newBuilder[Any]
      if (name != null) events += UserNameChanged(name)
      if (address != null) events += UserAddressChanged(address) 
      EventSeq(events.result())
  }
}
