        return el -> {
            if (el.startsWith("deny:")) {
                deniedWords.add(el.substring(5));
                return Collections.emptyList();
            } else if (deniedWords.contains(el)) {
                return Collections.emptyList();
            } else {
                return Collections.singletonList(el);
            }
        };
    })
    .runWith(Sink.seq(), system);
