  <dependencies>
    <dependency>
      <groupId>com.typesafe.akka</groupId>
      <artifactId>akka-bom_${scala.binary.version}</artifactId>
      <version>$akka.version$</version>
      <type>pom</type>
      <scope>import</scope>
