private CompletionStage<Done> removeItem(String userId, String itemId) {\n  Update<Cart> update =\n      new Update<>(\n          DataKey.create(userId),\n          Cart.empty(),\n          writeMajority,\n          curr -> curr.removeItem(itemId));\n  return askGet(new Get<Cart>(DataKey.create(userId), readMajority))\n      .thenCompose(\n          response -> {\n            if (response instanceof GetSuccess || response instanceof NotFound) {\n              return askUpdate(update);\n            } else {\n              throw new IllegalStateException(\"Get failed\");\n            }\n          })\n      .thenApply(updateDone -> Done.getInstance());\n}
