class EventDeserializationSkipped(val manifest: String)

class RemovedEventsAwareSerializer extends SerializerWithStringManifest {
  val removedTypes = Set("customer-blinked", "customer-ignored")

  override def manifest(o: AnyRef): String = o.getClass.getName

  override def fromBinary(bytes: Array[Byte], manifest: String): AnyRef = {
    manifest match {
      case removed if removedTypes.contains(removed) =>
        new EventDeserializationSkipped(manifest)
      case other =>
        // actual deserialization
        ...
    }
  }

  override def toBinary(o: AnyRef): Array[Byte] = ...
}
