package customer.domain;

import akka.javasdk.annotations.TypeName;

public sealed interface CustomerEvent {

  @TypeName("customer-created")
  record CustomerCreated(String name, String email, String address) implements CustomerEvent {}

  @TypeName("customer-name-changed")
  record CustomerNameChanged(String newName) implements CustomerEvent {}

  @TypeName("customer-email-changed")
  record CustomerEmailChanged(String newEmail) implements CustomerEvent {}

  @TypeName("customer-address-changed")
