private async Task AcquireLease()
{
    try
    {
        var acquired = await _lease.Acquire(exception =>
        {
            Self.Tell(new LeaseLost());
        });
        Self.Tell(acquired ? new AcquireLeaseSuccess() : new AcquireLeaseFailed());
    }
    catch (Exception)
    {
        Self.Tell(new AcquireLeaseFailed());
    }
}
