TITLE: Default HOCON Configuration for Akka.NET
DESCRIPTION: The base configuration file for Akka.NET that defines default settings for actor systems, dispatchers, serialization, remote actors, and other core framework components. This HOCON configuration serves as the foundation for any Akka.NET application.
SOURCE: https://github.com/akkadotnet/akka.net/blob/dev/docs/articles/configuration/modules/akka.md#2025-04-16_snippet_0

LANGUAGE: hocon
CODE:
```
####################################
####################################
# Akka.NET Default Configuration #
####################################
####################################

# This is the reference config file that contains all the default settings.
# Make your edits/overrides in your application.conf.

akka {

    # Loggers to register at boot time (akka.event.Logging$DefaultLogger logs
    # to STDOUT)
    loggers = ["Akka.Event.DefaultLogger, Akka"]

    # Specifies the default loggers dispatcher
    loggers-dispatcher = "akka.actor.default-dispatcher"

    # Log level used by the configured loggers (see "loggers") as soon
    # as they have been started; before that, see "stdout-loglevel"
    # Options: OFF, ERROR, WARNING, INFO, DEBUG
    loglevel = "INFO"

    # Log level for the very basic logger activated during ActorSystem startup.
    # This logger prints the log messages to stdout (System.out).
    # Options: OFF, ERROR, WARNING, INFO, DEBUG
    stdout-loglevel = "WARNING"

    # Filter of log events that is used by the LoggingAdapter before
    # publishing log events to the eventStream.
    logging-filter = "Akka.Event.DefaultLoggingFilter, Akka"

    # Used to set the behavior of the ActorSystem when an actor terminates because of an exceptions. 
	actor {
		# Default timeout for IActorRef.Ask() and Tell redelivery requests
		ask-timeout = 10s

		# Default timeout for ActorSystem.ActorOf() calls
		creation-timeout = 20s

        # Default timeout for the guardian actor to terminate all children
        guardian-supervisor-strategy {
             termination-timeout = 5s
        }

		# Used as default dispatcher for all actors if not specified otherwise
		default-dispatcher {
			type = "Akka.Dispatch.DefaultDispatcher, Akka"

			# Must be one of the following
			# Dispatcher, PinnedDispatcher, or a FQCN to a class inheriting
			# MessageDispatcherConfigurator with a public constructor with
			# both com.typesafe.config.Config parameter and
			# akka.dispatch.DispatcherPrerequisites parameter.
			# PinnedDispatcher must be used together with executor=thread-pool-executor.
			type = Dispatcher

			# Which kind of ExecutorService to use for this dispatcher
			# Valid options:
			#   "fork-join-executor" requires a "parallelism-min", a "parallelism-factor" and a "parallelism-max" / "task-queue-size" parameter
			#   "thread-pool-executor" requires a "core-pool-size-min", a "core-pool-size-factor" and a "core-pool-size-max" parameter
			#   "custom-executor" requires a path to a class extending the Akka.Dispatch.SingleProducerConsumerQueue
			executor = "default-executor"

			# How long time the dispatcher will wait for new actors until it shuts down
			shutdown-timeout = 1s

			# Throughput defines the number of messages that are processed in a batch
			# before the thread is returned to the pool. Set to 1 for as fair as possible.
			throughput = 30

			# Throughput deadline for Dispatcher, set to 0 or negative for no deadline
			throughput-deadline-time = 0ms

			# If negative (or zero) then an unbounded mailbox is used (default)
			# If positive then a bounded mailbox is used and the capacity is set using the
			# property

			# SPECIAL CASE: 0 means an unbounded mailbox, but since Akka.NET is written in c# 
			# we have to use the int.maxvalue for it to be unbounded
			mailbox-capacity = -1

			# Specifies the timeout to add a new message to a mailbox that is full -
			# negative number means `block` (blocking mode, wait until queue has space)
			# 0 and positive numbers mean adding to the mailbox will fail immediately
			# after the time specified. This is only relevant for bounded mailboxes
			mailbox-push-timeout-time = 10s

			# For "fork-join-executor" only.
			# Min number of threads to cap factor-based parallelism number to.
			fork-join-executor {
				# The parallelism factor is used to determine thread pool size using the
				# following formula: ceil(available processors * factor). Resulting size
				# is then bounded by the parallelism-min and parallelism-max values.
				parallelism-factor = 1.0

				# Min number of threads to cap factor-based parallelism number to.
				parallelism-min = 8

				# Max number of threads to cap factor-based parallelism number to.
				parallelism-max = 16

				task-queue-size = 32768
			}

			# For "thread-pool-executor" only.
			# Allows configuration of a Threadpool for this dispatcher
			thread-pool-executor {
				# Keep alive time for threads
				keep-alive-time = 60s

				# Define a fixed thread pool size with this property. The corePoolSize
				# will not grow and shrink. This is the default for this executor.
				fixed-pool-size = 4

				# Min number of threads to cap factor-based corePoolSize number to.
				core-pool-size-min = 8

				# The core-pool-size-factor is used to determine corePoolSize using the
				# following formula: ceil(available processors * factor).
				# Resulting size is then bounded by the core-pool-size-min and
				# core-pool-size-max values.
				core-pool-size-factor = 2.0

				# Max number of threads to cap factor-based corePoolSize number to.
				core-pool-size-max = 16

				# Minimum number of threads to cap factor-based maximumPoolSize number to.
				max-pool-size-min = 8

				# The max-pool-size-factor is used to determine maximumPoolSize using the
				# following formula: ceil(available processors * factor)
				# The maximumPoolSize will not be less than corePoolSize.
				# It is only used if using a bounded task queue.
				max-pool-size-factor = 2.0

				# Max number of threads to cap factor-based maximumPoolSize number to.
				max-pool-size-max = 16

				task-queue-size = -1
                task-queue-type = LINKED
			}

			# For "custom-executor" only. Need to be a class extending the Akka.Dispatch.ThreadPoolExecutorConfigurator
			custom-executor {
				# Path to the class extending the Akka.Dispatch.ThreadPoolExecutorConfigurator
				executor-path = "namespace.of.class, assembly"
			}

			# Throughput for default Dispatcher, set to 1 for as fair as possible
			throughput = 30
		}

		# Dispatcher for running actors that perform blocking IO
		blocking-io-dispatcher {
			type = "Akka.Dispatch.DefaultDispatcher, Akka"
			executor = "thread-pool-executor"
			throughput = 10

			thread-pool-executor {
				fixed-pool-size = 20
			}
		}

		# What happens when: actor throws an exception, actor failed to receive a message
		# (used by ask/?, explicit receive timeout)
		default-receive-timeout = 10s

		# If the mailbox is a single consumer queue
		# the actor will be scheduled on a thread that processes the
		# mailbox. Otherwise, the actor will be scheduled on a thread
		# from the thread pool
		single-consumer-queue = on 

		synchronous-io-blackboxing = on

		guardian-supervisor-strategy = "Akka.Actor.DefaultSupervisorStrategy, Akka"

		# Serializes and deserializes (non-primitive) messages to ensure immutability,
		# this is only intended for testing. 
		# WARNING: do not change unless you know what you're doing.
		# Enabling developer-mode for production deployments is highly discouraged.
		developer {
            # enable function serialization for TestActorRef
            serialize-function = off

            # enable serialization for ReceiveTimeout
            serialize-receive-timeout = off

            # enable serialization of ActorPath
            serialize-actor-path = off

            # Enable telemetry for developer mode
            telemetry = off
		}

		instrumentation {
			# Whether or not to automatically generate actor metrics
			enabled = off
        }

		# Set to on to enable quarantine of OnFailure termination strategy
		# Attaches a MonitoredByCorrelationId to all messages processed by the actor
		# WARNING: do not change unless you know what you're doing
		quarantine-on-failure = off

		debug {
			# enable DEBUG logging of all AutoReceiveMessages (Kill, PoisonPill etc.)
			receive = off

			# enable DEBUG logging of actor lifecycle changes
			lifecycle = off

			# enable DEBUG logging of all LoggingFSMs for events, transitions and timers
			fsm = off

			# enable DEBUG logging of subscription changes on the eventStream
			event-stream = off

			# enable DEBUG logging of unhandled messages
			unhandled = off

			# enable WARN logging of misconfigured routers
			router-misconfiguration = off
		}
	}

    scheduler {
        # The LightArrayRevolverScheduler implementation is used as the default 
        # scheduler in the system. It does not execute the scheduled tasks on exact
        # time, but on every tick, it will run everything that is (over)due.
        # Ticks are scheduled using the configured "tick-duration" and is fixed
        # delay execution in the executor configured in "tick-executor".
        implementation = "Akka.Actor.LightArrayRevolverScheduler, Akka"

        # The scheduling interval for the Revolving Wheel.
        tick-duration = 200ms
        # The executor to schedule the ticks on.
        tick-executor = "default-executor"

        # When shutting down the scheduler, there will typically be a thread which
        # needs to be stopped, and this timeout determines how long to wait for
        # that to happen. In case of timeout the shutdown of the actor system will
        # proceed without running possibly still enqueued tasks.
        shutdown-timeout = 5s
    }

	# Serializes and deserializes actors using proto buf
	serial-versions = {}

	# Used as default serializer if no configured class can be matched to the class to be serialization
	# should be one of the serializers defined under akka.actor.serialization-bindings
	serializers {
		proto = "Akka.Serialization.ProtobufSerializer, Akka"
		json = "Akka.Serialization.NewtonSoftJsonSerializer, Akka"
		bytesbe = "Akka.Serialization.ByteArraySerializer, Akka"
		bytesmem = "Akka.Serialization.MemoryPoolByteArraySerializer, Akka"
		ack = "Akka.Serialization.AckSerializer, Akka"
	}

	# class name of binding => serializer to be used
	serilization-bindings {
		"System.Byte[]" = bytesmem
		"System.Object" = json
		"Google.ProtocolBuffers.IMessage, Google.ProtocolBuffers" = proto
		"Akka.Serialization.Serialization+AckISerializable, Akka" = ack
	}

	# Configuration for the default serializer
	serializer-config {
		# To be used with Akka.Serialization.AckSerializer
		ack {
			type = "Akka.Serialization.AckSerializer, Akka"
		}

		proto {
			type = "Akka.Serialization.ProtobufSerializer, Akka"
		}

		json {
			type = "Akka.Serialization.NewtonSoftJsonSerializer, Akka"

			indented = off

			# Number of objects cached by the serializer
			serializer-cache-size = 1024

			preserveObjectReferences = off
		}

		diagnostics {
			# Set to on to enable serializer-verification. When enabled, the serializer will
			# verify that a message can be serialized/deserialized. If it can't, the system
			# falls back to the previous serializer. If no serializers can serialize the message,
			# Akka will fall back to the default serializer.
			serializer-verification = off
		}
	}

	# Entries for pluggable serializers and their bindings.
	serializers {}

	# Class to Serializer binding. You only need to specify the name of an interface
	# or class here if you want it to be serialized when it's sent across the wire.
	# Classes extending ISerializable are always serialized, so they don't need to be
	# included here.
	serilization-bindings {}

	# Additional serialization settings
	serializer-settings {}

	execution-context {
		# The ID of the bounded mailbox to be used by the we dispatcher 
		default-mailbox {
			# Any MailboxType can be used here, for example: Akka.Dispatch.SingleConsumerOnlyUnboundedMailbox
			mailbox-type = ""
			# If the mailbox is bounded then it uses this setting to determine its capacity
			mailbox-capacity = 1000
			# If the mailbox is bounded then this is the timeout for enqueueing
			mailbox-push-timeout-time = 10s
		}

		# How long time the dispatcher will wait for new actors until it shuts down
		default-shutdown-timeout = 1s
	}

	# Used to set the behavior of the AkkaSystem, wether its responsible for 
	# creating the Threadpool for your actors. 
	default-executor {
		# Valid options: fork-join-executor, thread-pool-executor,custom-executor
		executor = "fork-join-executor"
	}

	# Configuration namespace of default threadpool-based dispatcher
	thread-pool-executor {
			# Keep alive time for threads
		keep-alive-time = 60s

		# Define a fixed thread pool size with this property. The corePoolSize
		# will not grow and shrink. This is the default for this executor.
		fixed-pool-size = 4

		# Min number of threads to cap factor-based corePoolSize number to.
		core-pool-size-min = 8

		# The core-pool-size-factor is used to determine corePoolSize using the
		# following formula: ceil(available processors * factor).
		# Resulting size is then bounded by the core-pool-size-min and
		# core-pool-size-max values.
		core-pool-size-factor = 2.0

		# Max number of threads to cap factor-based corePoolSize number to.
		core-pool-size-max = 16

		# Minimum number of threads to cap factor-based maximumPoolSize number to.
		max-pool-size-min = 8

		# The max-pool-size-factor is used to determine maximumPoolSize using the
		# following formula: ceil(available processors * factor)
		# The maximumPoolSize will not be less than corePoolSize.
		# It is only used if using a bounded task queue.
		max-pool-size-factor = 2.0

		# Max number of threads to cap factor-based maximumPoolSize number to.
		max-pool-size-max = 16

		task-queue-size = 32768
        task-queue-type = LINKED
	}

	# Configuration namespace of fork-join-executor dispatcher
	fork-join-executor {
		# Min number of threads to cap factor-based parallelism number to.
		parallelism-min = 8

		# The parallelism factor is used to determine thread pool size using the
		# following formula: ceil(available processors * factor). Resulting size
		# is then bounded by the parallelism-min and parallelism-max values.
		parallelism-factor = 1.0

		# Max number of threads to cap factor-based parallelism number to.
		parallelism-max = 16

		task-queue-size = 32768
	}

	# Value types providers, only used for serialization (with typehints) so far
	value-types-providers {
	}

    # Serialization uses serializer-bindings as configuration
    serialization {
        # This setting configures serialization queues and how many operations it can
        # fit in a single ActorMessage (processed at once).
        throughput = 10
    }

	# Used to configure Loggers
	logger-startup-timeout = 30s

	exceptions {
		# Fully qualified class name of the UnhandledExceptionHandler
		# The handler is used for logging and the message of the exception will be thrown out
		unhandled {
			handler-class = "Akka.Event.DefaultUnhandledExceptionHandler, Akka"
		}
	}

	dot-net {
		# Objects sent in AskDotNet operations will be wrapped in this type name.
		serializer-wrapper-name = "AskValueWrapper"
	}

    # Configuration of the transport used to communicate with other nodes in the cluster
    remote {
        # Default configuration which applies to all of the transport types
        transport-failure-detector {
            # FQCN of the failure detector implementation.
            # It must implement akka.remote.FailureDetector and have
            # a public constructor with a com.typesafe.config.Config and
            # akka.actor.EventStream parameter.
            implementation-class = "Akka.Remote.DeadlineFailureDetector, Akka.Remote"

            # How often keep-alive heartbeat messages should be sent to each connection.
            heartbeat-interval = 4 s

            # Number of potentially lost/delayed heartbeats that will be
            # accepted before considering it to be an anomaly.
            # A margin to the +/- 50% failure detection threshold is important to
            # be able to survive sudden, occasional, pauses in heartbeat arrivals,
            # due to for example garbage collect or network drop.
            acceptable-heartbeat-pause = 20 s
        }

        # Settings for the Phi accrual failure detector (used for remote death watch) 
        watch-failure-detector {
            # FQCN of the failure detector implementation; Inherits from MultipleAssociationsFailureDetector
            implementation-class = "Akka.Remote.PhiAccrualFailureDetector, Akka.Remote"

            # How often keep-alive heartbeat messages should be sent to each connection.
            heartbeat-interval = 1 s

            # Defines the failure detector threshold; A low threshold is prone to generate many false positives, while a high threshold will generate false negatives.
            # The threshold usually triggered by a sudden, steep increase in response latency rather than a mild, sustained increase in latency.
            # The phi accrual failure detector measures this latency and filter by what is considered "normal", allowing some room for sustained latency increase.
            # The default threshold for a 1 second heartbeat interval is 8 and that is considered to be a good default value. Adjust to the same value your transport-failure-detector threshold (in this case 12)
            threshold = 12.0

            # Number of potentially lost/delayed heartbeats that will be
            # accepted before considering it to be an anomaly.
            # A margin to the +/- 50% failure detection threshold is important to
            # be able to survive sudden, occasional, pauses in heartbeat arrivals,
            # due to for example garbage collect or network drop.
            acceptable-heartbeat-pause = 10 s

            # Maximum burst time window, for PhiAccrualFailureDetector
            min-std-deviation = 100 ms

            # Configures the size of the max-sample-size ring buffer
            # Sample window to keep data
            max-sample-size = 1000

            # Minimum size of window we'd like to use
            min-samples = 2
        }

        ### Configuration for the Gossip failure detector to check remote systems for stability
        system-message-buffer-size = 1000

        # This failure detector will be used for in remote death watch
        system-failure-detector = {

            # FQCN of the failure detector implementation; Inherits from MultipleAssociationsFailureDetector
            implementation-class = "Akka.Remote.PhiAccrualFailureDetector, Akka.Remote"

            # How often keep-alive heartbeat messages should be sent to each connection.
            heartbeat-interval = 1 s

            # Defines the failure detector threshold;  A low threshold is prone to generate many false positives, while a high threshold will generate false negatives.
            # The threshold usually triggered by a sudden, steep increase in response latency rather than a mild, sustained increase in latency.
            # The phi accrual failure detector measures this latency and filter by what is considered "normal", allowing some room for sustained latency increase.
            # The default threshold for a 1 second heartbeat interval is 8 and that is considered to be a good default value.
            threshold = 8.0

            # Number of potentially lost/delayed heartbeats that will be
            # accepted before considering it to be an anomaly.
            # A margin to the +/- 50% failure detection threshold is important to
            # be able to survive sudden, occasional, pauses in heartbeat arrivals,
            # due to for example garbage collect or network drop.
            acceptable-heartbeat-pause = 10 s

            # Maximum burst time window, for PhiAccrualFailureDetector
            min-std-deviation = 100 ms

            # Configures the size of the max-sample-size ring buffer
            # Sample window to keep data
            max-sample-size = 1000

            # Minimum size of window we'd like to use
            min-samples = 2
        }

        # How many retries should be made to a node, before it's being disconnected
        retry-window = 3s
        # Time between retries.
        retry-delay = 1s
        # Maximum number of connection attempts before unreachable connection
        maximum-retries-in-window = 5
    }
}
