    private void Angry()
    {
        Receive<string>(s => s.Equals("foo"), msg =>
        {
            Sender.Tell("I am already angry?");
        });

        Receive<string>(s => s.Equals("bar"), msg =>
        {
            Become(Happy);
        });
    }

    private void Happy()
    {
        Receive<string>(s => s.Equals("foo"), msg =>
        {
            Sender.Tell("I am already happy :-)");
        });

        Receive<string>(s => s.Equals("bar"), msg =>
        {
            Become(Angry);
        });
    }
}
