var connection = Tcp.Get(system).OutgoingConnection("127.0.0.1", 8888);

var repl = Flow.Create<ByteString>()
    .Via(Framing.Delimiter(
        ByteString.FromString("\n"),
        maximumFrameLength: 256,
        allowTruncation: true))
    .Select(text =>
    {
        Console.WriteLine("Server: " + text.DecodeString());
        Console.Write("Client: ");
        return ByteString.FromString(Console.ReadLine() + "\n");
    });

var task = Source.Repeat(ByteString.Empty)
    .Via(connection)
    .Join(repl)
    .Run(materializer);

await task;
```

