LANGUAGE: csharp
CODE:
```
public class Subscriber : ReceiveActor
{
    private readonly IActorRef _mediator;

    public Subscriber()
    {
        // Get a reference to the mediator
        _mediator = DistributedPubSub.Get(Context.System).Mediator;

        // subscribe to the topic named "content"
        _mediator.Tell(new Subscribe("content", Self));

        Receive<string>(s => 
        {
            Console.WriteLine($"Got {s}");
        });

        Receive<SubscribeAck>(subscribeAck => 
        {
            Console.WriteLine($"subscribing to {subscribeAck.Subscribe.Topic}");
        });
    }
}
