TITLE: Complete DeviceGroupQuery Implementation in Akka.NET
DESCRIPTION: Full implementation of the DeviceGroupQuery actor that collects temperature readings from device actors with timeout handling, response tracking, and result aggregation. It uses Context.Become() for state management.
SOURCE: https://github.com/akkadotnet/akka.net/blob/dev/docs/articles/intro/getting-started/tutorial-4.md#2025-04-16_snippet_4

LANGUAGE: csharp
CODE:
```
public class DeviceGroupQuery : UntypedActor
{
    private ICancelable queryTimeoutTimer;

    public DeviceGroupQuery(
        Dictionary<string, IActorRef> actorToDeviceId,
        long requestId,
        IActorRef requester,
        TimeSpan timeout)
    {
        ActorToDeviceId = actorToDeviceId;
        RequestId = requestId;
        Requester = requester;
        Timeout = timeout;

        queryTimeoutTimer = Context.System.Scheduler.ScheduleTellOnceCancelable(
            timeout,
            Self,
            new CollectionTimeout(),
            Self);

        foreach (var deviceActor in ActorToDeviceId.Values)
        {
            Context.Watch(deviceActor);
            deviceActor.Tell(new ReadTemperature(0L));
        }
    }

    protected override void OnReceive(object message)
    {
        var repliesSoFar = new Dictionary<string, TemperatureReading>();
        var stillWaiting = new HashSet<IActorRef>(ActorToDeviceId.Values);

        Context.Become(WaitingForReplies(repliesSoFar, stillWaiting));

        // We need the first handler to be active immediately as there might be responses arriving
        // even before we get scheduled to run the first time, so we manually call the handler
        Receive(message);
    }

    private UntypedReceive WaitingForReplies(
        Dictionary<string, TemperatureReading> repliesSoFar,
        HashSet<IActorRef> stillWaiting)
    {
        return message =>
        {
            if (message is RespondTemperature response)
            {
                var deviceActor = Sender;
                ReceivedResponse(deviceActor, response.RequestId, response.Temperature, repliesSoFar, stillWaiting);
            }
            else if (message is Terminated t)
            {
                var deviceActor = t.ActorRef;
                ReceivedResponse(deviceActor, 0L, null, repliesSoFar, stillWaiting);
            }
            else if (message is CollectionTimeout)
            {
                var replies = new Dictionary<string, TemperatureReading>(repliesSoFar);
                foreach (var deviceActor in stillWaiting)
                {
                    var deviceId = ActorToDeviceId[deviceActor];
                    replies.Add(deviceId, TemperatureNotAvailable.Instance);
                }

                Requester.Tell(new RespondAllTemperatures(RequestId, replies));
                Context.Stop(Self);
            }
        };
    }

    private void ReceivedResponse(
        IActorRef deviceActor,
        long requestId,
        double? temperature,
        Dictionary<string, TemperatureReading> repliesSoFar,
        HashSet<IActorRef> stillWaiting)
    {
        Context.Unwatch(deviceActor);
        var deviceId = ActorToDeviceId[deviceActor];

        stillWaiting.Remove(deviceActor);

        TemperatureReading reading;
        if (temperature.HasValue)
        {
            reading = new Temperature(temperature.Value);
        }
        else
        {
            reading = DeviceNotAvailable.Instance;
        }

        repliesSoFar.Add(deviceId, reading);

        if (stillWaiting.Count == 0)
        {
            Requester.Tell(new RespondAllTemperatures(RequestId, repliesSoFar));
            Context.Stop(Self);
        }
        else
        {
            Context.Become(WaitingForReplies(repliesSoFar, stillWaiting));
        }
    }

    protected override void PostStop()
    {
        queryTimeoutTimer.Cancel();
    }

    public Dictionary<string, IActorRef> ActorToDeviceId { get; }
    public long RequestId { get; }
    public IActorRef Requester { get; }
    public TimeSpan Timeout { get; }
}
```

