public class RestartNode2Spec : MultiNodeClusterSpec
{
    private readonly RestartNode2SpecConfig _config;

    public RestartNode2Spec() : this(new RestartNode2SpecConfig()) { }

    protected RestartNode2Spec(RestartNode2SpecConfig config) : base(config)
    {
        _config = config;
    }

    private ImmutableList<Address> SeedNodes
    {
        get { return ImmutableList.Create(GetAddress(_config.Seed1), GetAddress(_config.Seed2)); }
    }

    [MultiNodeFact]
    public void Some_test_name()
    {
        RunOn(() =>
        {
            Cluster.JoinSeedNodes(SeedNodes);
            Within(TimeSpan.FromSeconds(15), () =>
            {
                AwaitAssert(() =>
                {
                    Assert.Equal(2, Cluster.State.Members.Count);
                    Assert.Equal(MemberStatus.Up, Cluster.State.Members.First().Status);
                    Assert.Equal(MemberStatus.Up, Cluster.State.Members.Skip(1).First().Status);
                });
            });
        }, _config.Seed2);

        RunOn(() =>
        {
            Cluster.JoinSeedNodes(SeedNodes);
            Within(TimeSpan.FromSeconds(15), () =>
            {
                AwaitAssert(() =>
                {
                    Assert.Equal(2, Cluster.State.Members.Count);
                    Assert.Equal(MemberStatus.Up, Cluster.State.Members.First().Status);
                    Assert.Equal(MemberStatus.Up, Cluster.State.Members.Skip(1).First().Status);
                });
            });
        }, _config.Seed1);

        EnterBarrier("cluster started");
    }
}
