LANGUAGE: csharp
CODE:
```
[Fact]
public void UnsolvableSubstitutionWillThrowTest()
{
    var exception = Assert.Throws<HoconException>(() =>
    {
        var hocon = @"{
                a: ${does.not.exist}
            }";
        var config = ConfigurationFactory.ParseString(hocon);
        return config.GetString("a");
    });

    Assert.Contains("Could not resolve substitution", exception.Message);
}
