TITLE: Default HOCON Configuration for Akka.Remote Package
DESCRIPTION: The default configuration settings for Akka.Remote in HOCON format. These settings define transport protocols, remote deployment, watch failure detection parameters, and other remote communication settings for Akka.NET.
SOURCE: https://github.com/akkadotnet/akka.net/blob/dev/docs/articles/configuration/modules/akka.remote.md#2025-04-16_snippet_0

LANGUAGE: HOCON
CODE:
```
akka.remote {
    dot-netty.tcp {
        transport-class = "Akka.Remote.Transport.DotNetty.TcpTransport, Akka.Remote"
        applied-adapters = []
        transport-protocol = tcp
        port = 2552
        hostname = localhost
        public-hostname = "POPULATE"
        bind-port = 0
        bind-hostname = ""
        ssl {
            suppress-validation = false
            certificate {
                # valid certificate options: auto | manual
                # auto - automatically creates a self-signed certificate
                # manual - requires user to specify certificate parameters
                mode = "auto"
                # WARNING!: AUTO-GENERATED CERTIFICATES FOR SSL WILL ONLY WORK ON LOCALHOST
                # Hostname must be specified when using auto-signed certificates otherwise generated certificate 
                # will only operate on "localhost".
                password = ""
                # path-to-certificate-store (PKCS12 format) - manual only
                path = ""
                # X.509 subject names - manual only
                subject {
                    common-name = ""
                    # optional values
                    organization = ""
                    organization-unit = ""
                }
                # algorithm with which this certificate was generated
                signature-algorithm = "SHA256withRSA"
                # minimum RSA key length
                key-size = 4096
                # time range of validity
                validity {
                    from = "2019-01-01 00:00:00.000"
                    # duration in days
                    duration = 365
                }
            }
            # Valid values: "TLS" (default), "TLSv1", "TLSv1.1", "TLSv1.2"
            protocol = "TLS"
        }

        # Sets the send buffer size of the Sockets,
        # set to 0b for platform default
        send-buffer-size = 256000b

        # Sets the receive buffer size of the Sockets,
        # set to 0b for platform default
        receive-buffer-size = 256000b

        # Maximum message size the transport will accept, but at least 32000b.
        # Please note that UDP does not support arbitrary large datagrams,
        # so this setting has to be chosen carefully when using UDP.
        # Both send-buffer-size and receive-buffer-size settings has to
        # be adjusted to be able to buffer messages of maximum size.
        maximum-frame-size = 128000b

        # Sets the size of the connection backlog
        backlog = 100

        # The TcpTransport reconnection interval
        reconnect-timeout = 30s

        # Enables the TCP_NODELAY flag, i.e. disables Nagle's algorithm
        tcp-nodelay = on

        # Enables TCP Keepalive, subject to the O/S kernel's configuration
        tcp-keepalive = on

        # Enables SO_REUSEADDR, which determines when an ActorSystem can open
        # the specified listen port (the meaning differs between *nix and Windows).
        # Valid values are "off", "on", and "on-close" corresponding to the values
        # in Microsoft's SO_REUSEADDR documentation.
        # Further explanation of values on Windows:
        # off - consecutive ActorSystems can only use an address if the previous 
        #    user is gone
        # on - consecutive ActorSystems can use an address even if the previous
        #    user is still around, but only if the protocol (TCP) is different
        # on-close - consecutive ActorSystems can use an address even if the 
        #    previous user is still around, using the same protocol (TCP)
        # on-close is the default value on Windows. For Unix-like systems, this
        # setting is ignored and the SO_REUSEADDR is unconditionally enabled.
        reuse-addr = off        
    }

    dot-netty.udp {
        transport-class = "Akka.Remote.Transport.DotNetty.UdpTransport, Akka.Remote"
        applied-adapters = []
        transport-protocol = udp
        port = 2552
        hostname = localhost
        public-hostname = "POPULATE"
        # Sets the send buffer size of the Sockets,
        # set to 0b for platform default
        send-buffer-size = 256000b

        # Sets the receive buffer size of the Sockets,
        # set to 0b for platform default
        receive-buffer-size = 256000b

        # Maximum message size the transport will accept, but at least 32000b.
        # Please note that UDP does not support arbitrary large datagrams,
        # so this setting has to be chosen carefully when using UDP.
        # Both send-buffer-size and receive-buffer-size settings has to
        # be adjusted to be able to buffer messages of maximum size.
        maximum-frame-size = 128000b

        # Sets the size of the connection backlog
        backlog = 4
    }

    # DEPRECATED
    helios.tcp {
        transport-class = "Akka.Remote.Transport.Helios.HeliosTcpTransport, Akka.Remote"
        applied-adapters = []
        transport-protocol = tcp
        port = 2552
        hostname = localhost
        public-hostname = "POPULATE"

        # Sets the send buffer size of the Sockets,
        # set to 0b for platform default
        send-buffer-size = 256000b

        # Sets the receive buffer size of the Sockets,
        # set to 0b for platform default
        receive-buffer-size = 256000b

        # Maximum message size the transport will accept, but at least 32000b.
        # Please note that UDP does not support arbitrary large datagrams,
        # so this setting has to be chosen carefully when using UDP.
        # Both send-buffer-size and receive-buffer-size settings has to
        # be adjusted to be able to buffer messages of maximum size.
        maximum-frame-size = 128000b

        # Sets the size of the connection backlog
        backlog = 4

        # Enables TCP Keepalive, subject to the O/S kernel's configuration
        tcp-keepalive = on

        # Enables the TCP_NODELAY flag, i.e. disables Nagle's algorithm
        tcp-nodelay = on
    }

    retry-gate-closed-for = 5s

    # time after which a joining member that has not been accepted will shutdown
    joining-timeout = 5s 

    # If this is "on", Akka will log all inbound messages at DEBUG level,
    # if off then they are not logged
    log-received-messages = off

    # If this is "on", Akka will log all outbound messages at DEBUG level,
    # if off then they are not logged
    log-sent-messages = off

    # If this is "on", Akka will log all UNSERIALIZABLE messages at INFO level
    log-remote-lifecycle-events = on

    # Each transport adapters can have several adapters attached to it
    # An adapter provides specific serialization and deserialization capabilities
    # which can be utilized for Remote Actor Communications.
    # Some of them are used by Akka internally while others are to be provided by the user.
    ### Built in: 
    # akka.remote.serialization-bindings {
    # "System.Object" = akka-actors
    # }
    serializers {
        akka-containers = "Akka.Remote.Serialization.MessageContainerSerializer, Akka.Remote"
    }
    serialization-bindings {
        "Akka.Remote.Serialization.WrappedMessage, Akka.Remote" = akka-containers
        "Akka.Remote.Serialization.MessageContainerByteString, Akka.Remote" = akka-containers
        "Akka.Remote.Serialization.MessageContainerJava, Akka.Remote" = akka-containers
        "Akka.Remote.DaemonMsgCreate, Akka.Remote" = akka-containers
    }

    # The hostname or ip to bind the remoting to,
    # InetAddress.getLocalHost.getHostAddress is used if empty
    hostname = localhost

    # For resiliency and network error recovery, set to on
    use-dispatcher-for-io = on

    # Name of the remote dispatcher to be used by the transport
    use-dispatcher = akka.remote.default-remote-dispatcher

    # Default remote dispatcher for all used transports
    # Default local dispatcher to use for Remote operations: default-dispatcher
    default-remote-dispatcher {
        type = Dispatcher
        executor = "fork-join-executor"
        fork-join-executor {
            # Min number of threads to cap factor-based parallelism number to
            parallelism-min = 2
            # The parallelism factor is used to determine thread pool size using the
            # following formula: ceil(available processors * factor). Resulting size
            # is then bounded by the parallelism-min and parallelism-max values.
            parallelism-factor = 0.5
            # Max number of threads to cap factor-based parallelism number to
            parallelism-max = 24
        }
        throughput = 100
    }

    ### Security - transport security key generation
    secure-cookie {
        # Generate a new key on each node start
        # Overrides the key-secret value
        generate-new-key = off
        # Should a created key be placed in the agent directory
        # WARNING: Key is placed in plain text
        save-to-disk = off
        # An intermediate seed key for the key generator algorithm
        # WARNING: Do not change unless you know what you're doing.
        key-secret = "a-random-seed-value0123-ABCDEF"
        # The algorithm to use for the key generator
        algorithm = "AES"
    }

    ### Security - remote TLS
    security {
        # This is the exact same config that can be specified once per port under dot-netty.tcp.
        # The settings here act as defaults to those, if left unspecified.
        ssl {
            suppress-validation = false
            certificate {
                # valid certificate options: auto | manual
                # auto - automatically creates a self-signed certificate
                # manual - requires user to specify certificate parameters
                mode = "auto"
                # WARNING!: AUTO-GENERATED CERTIFICATES FOR SSL WILL ONLY WORK ON LOCALHOST
                # Hostname must be specified when using auto-signed certificates otherwise generated certificate 
                # will only operate on "localhost".
                password = ""
                # path-to-certificate-store (PKCS12 format) - manual only
                path = ""
                # X.509 subject names - manual only
                subject {
                    common-name = ""
                    # optional values
                    organization = ""
                    organization-unit = ""
                }
                # algorithm with which this certificate was generated
                signature-algorithm = "SHA256withRSA"
                # minimum RSA key length
                key-size = 4096
                # time range of validity
                validity {
                    from = "2019-01-01 00:00:00.000"
                    # duration in days
                    duration = 365
                }
            }
            # Valid values: "TLS" (default), "TLSv1", "TLSv1.1", "TLSv1.2"
            protocol = "TLS"
        }
    }

    ### Configuration of the Phi accrual failure detector algorithm.
    ### Refer to Akka.Remote.PhiAccrualFailureDetector for more information.
    watch-failure-detector {
        # FQCN of the failure detector implementation.
        # It must implement akka.remote.FailureDetector and have
        # a public constructor with a com.typesafe.config.Config and
        # akka.actor.EventStream parameter.
        implementation-class = "Akka.Remote.PhiAccrualFailureDetector, Akka.Remote"

        # How often keep-alive heartbeat messages should be sent to each connection.
        heartbeat-interval = 1s

        # Threshold used for the phi failure detector. Should be set lower for faster failure detection
        # but to a higher value to be able to survive network congestion / GC pause
        threshold = 10.0

        # Number of potentially lost/delayed heartbeats that will be accepted before considering it to be an anomaly.
        # This margin is important to be able to survive sudden, occasional, pauses in heartbeat arrivals,
        # due to for example garbage collection or network drop outs.
        max-sample-size = 200

        # Minimum number of samples that the failure detector requires to asses the health of a connection
        min-std-deviation = 100ms

        # Number of samples of inter-heartbeat arrival times to adaptively calculate the failure timeout for connections.
        acceptable-heartbeat-pause = 10s

        # Automatically correct clocks that would otherwise force the failure detector to mark connections as failed, even
        # in the presence of regular heartbeats.
        # DANGEROUS: In production you should be very careful about enabling this since it can mask real crashed.
        expected-response-after = 2s
    }

    ### Configuration of the automatic transport-layer failure injector.
    # The injector will impose failure conditions on the remoting subsystem, which is useful to
    # test the resilience of your system in the presence of network failures.
    # It does support the injection of any of the following fault kinds:
    # 1) Disabling packet transfer between a pair of endpoints completely;
    # 2) Inducing message losses;
    # 3) Adding random delays (latency) to messages within specified parameters.
    transport-failure-detector {
        # FQCN of the failure detector implementation.
        # It must implement akka.remote.FailureDetector and have
        # a public constructor with a com.typesafe.config.Config and
        # akka.actor.EventStream parameter.
        implementation-class = "Akka.Remote.DeadlineFailureDetector, Akka.Remote"

        # How often keep-alive heartbeat messages should be sent to each connection.
        heartbeat-interval = 4s

        # Number of potentially lost/delayed heartbeats that will be accepted before considering it to be an anomaly.
        # A margin of 10 messages corresponds to roughly 40 seconds with a heartbeat interval of 4 seconds.
        acceptable-heartbeat-pause = 40s

        # Maximum time under which an acknowledged packet must be received for the 
        # DeadlineFailureDetector.
        expected-response-after = 8s
    }

    command-ack-timeout=30s

    backoff-remote-dispatcher-failure = 0s

    prune-quarantine-marker-after = 5d

    system-message-ack-piggyback-timeout = 3s

    system-message-buffer-size = 1000

    systemmessage-resend-interval = 100ms

    initial-system-message-delivery-timeout = 3m

    resend-interval = 2s

    enabled-transports = ["akka.remote.dot-netty.tcp"]

    maximum-retries-in-window = 20

    retry-window = 5s

    prune-quarantine-marker-after = 5d

    gate-invalid-addresses-for = 5s
}

akka.actor.deployment {
