TITLE: Complete Sign In Component with OTP Flow in React Native
DESCRIPTION: This complete sign-in component implements the full OTP authentication flow. It handles sending the OTP to the user's email and shows a modal for entering the verification code. The component utilizes the useAuthenticate and useUser hooks from Account Kit.
SOURCE: https://github.com/alchemyplatform/aa-sdk/blob/main/site/pages/react-native/signer/authenticating-users/authenticating-with-otp.mdx#2025-04-22_snippet_2

LANGUAGE: tsx
CODE:
```
// @noErrors
import React, { useCallback, useState } from "react";
import { View, Text, TextInput, Button } from "react-native";
import { useAuthenticate, useUser } from "@account-kit/react-native";

import { OtpPopUp } from "./otp-popup";

export const SignInWithOtp = () => {
  const [email, setEmail] = useState<string>("");
  const [showOtp, setShowOtp] = useState<boolean>(false);

  const [loading, setLoading] = useState<boolean>(false);
  const { authenticate } = useAuthenticate();
  const { user } = useUser();

  // Make an authentication request to a user's email
  const performAuthRequest = useCallback(
    (email: string) => {
      try {
        authenticate({
          email,
          type: "email",
          emailMode: "otp",
        });

        setLoading(true);
        setShowOtp(true);
      } catch (e) {
        Alert.alert("Error sending OTP Code. Check logs for more details.");

        console.log("Error seding OTP CODE: ", e);
      }
    },
    [authenticate]
  );

  const completeAuth = useCallback(() => {
    setLoading(false);
    setShowOtp(false);
  }, []);

  return (
    <View>
      {user && (
        <>
          <Text>User Authenticated As: {user.email}</Text>
          <Text>{user.address}</Text>
        </>
      )}

      <Text style={{ fontSize: 16 }}>Enter Email</Text>
      <TextInput
        value={email}
        style={{ fontSize: 20 }}
        onChangeText={setEmail}
        placeholder="Enter Email"
        autoCapitalize="none"
      />
      <Button
        title={loading ? "Loading" : "Sign In"}
        disabled={loading}
        onPress={() => performAuthRequest(email)}
      />

      <OtpPopUp
        show={showOtp}
        completeAuth={completeAuth}
        close={() => {
          setShowOtp(false);
          setLoading(false);
        }}
      />
    </View>
  );
```

