const builder = PermissionBuilder.create({
  validAfter: 0n,
  validUntil: 0n,
})
  .setAccessListType("ALLOWLIST")
  .setAccessControlEntry({
    address: contractAddress,
    isOnList: true,
    checkSelectors: true,
  })
  .setAccessControlEntrySelector({
    address: contractAddress,
