TITLE: Handling Email OTP Verification in React
DESCRIPTION: Implements the OTP verification process using the useSignerStatus and useAuthenticate hooks. Monitors authentication status and handles OTP submission.
SOURCE: https://github.com/alchemyplatform/aa-sdk/blob/main/site/pages/react/mfa/email-otp.mdx#2025-04-22_snippet_1

LANGUAGE: tsx
CODE:
```
import { useSignerStatus, useAuthenticate } from "@account-kit/react";
import { AlchemySignerStatus } from "@account-kit/signer";
import React, { useEffect } from "react";

function EmailOtpVerification() {
  const { status } = useSignerStatus();
  const { authenticate, isPending } = useAuthenticate({
    onError: (error) => {
      // Handle OTP verification errors
      console.error("OTP verification failed:", error);
    },
  });

  // Called when user enters their OTP code from email
  const handleVerify = (emailOtp: string) => {
    authenticate({
      type: "otp",
      otpCode: emailOtp,
    });
  };

  // Example of prompting user when OTP verification is needed
  useEffect(() => {
    if (status === AlchemySignerStatus.AWAITING_EMAIL_AUTH) {
      // Show OTP input UI to the user
    }
  }, [status]);

  return (
    // Your OTP input UI
    <div>{/* OTP input component */}</div>
  );
}
