TITLE: OTP Verification Modal Component in React Native
DESCRIPTION: This modal component handles the OTP verification part of the authentication flow. It displays a form for the user to enter the OTP code received via email and verifies it using the authenticate function with type 'otp' from Account Kit.
SOURCE: https://github.com/alchemyplatform/aa-sdk/blob/main/site/pages/react-native/signer/authenticating-users/authenticating-with-otp.mdx#2025-04-22_snippet_3

LANGUAGE: tsx
CODE:
```
// @noErrors
import React, { useCallback, useState } from "react";
import {
  Modal,
  Text,
  TextInput,
  Button,
  SafeAreaView,
  Alert,
} from "react-native";
import { useAuthenticate } from "@account-kit/react-native";

export const OtpPopUp = ({
  show,
  completeAuth,
  close,
}: {
  show: boolean;
  completeAuth: () => void;
  close: () => void;
}) => {
  const { authenticate } = useAuthenticate();
  const [otpCode, setOtpCode] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(false);

  // Authenticate a user using a bundle returned from a deep link
  const handleUserOtp = useCallback(
    (otpCode: string) => {
      try {
        setLoading(true);
        authenticate({ otpCode, type: "otp" }); //<-- Pass the user's OTP code to the authenticate method using `otp` as the type value

        completeAuth();
      } catch (e) {
        Alert.alert("Error verifying OTP Code. Check logs for more details.");

        console.log("Error verifying OTP CODE: ", e);
      }
    },
    [authenticate]
  );

  return (
    <Modal visible={show} style={{ paddingTop: 200 }}>
      <SafeAreaView style={{ margin: 20 }}>
        <Text style={{ fontSize: 16 }}>Enter OTP</Text>
        <TextInput
          style={{ fontSize: 20 }}
          value={otpCode}
          onChangeText={setOtpCode}
          placeholder="Enter OTP"
        />
        <Button
          title={loading ? "Loading" : "Submit OTP"}
          disabled={loading}
          onPress={() => handleUserOtp(otpCode)}
        />
        <Button
          title="Close"
          onPress={() => {
            setLoading(false);
            close();
          }}
        />
      </SafeAreaView>
    </Modal>
  );
};
```

