TITLE: OTP Popup Component for React Native
DESCRIPTION: This snippet shows the implementation of an OTP popup component in React Native. It handles the OTP verification process using the Account Kit SDK and includes UI elements for OTP input and submission.
SOURCE: https://github.com/alchemyplatform/aa-sdk/blob/main/docs/pages/react-native/signer/authenticating-users/authenticating-with-otp.mdx#2025-04-22_snippet_3

LANGUAGE: tsx
CODE:
```
import React, { useCallback, useState } from "react";
import {
  Modal,
  Text,
  TextInput,
  Button,
  SafeAreaView,
  Alert,
} from "react-native";
import { useAuthenticate } from "@account-kit/react-native";

export const OtpPopUp = ({
  show,
  completeAuth,
  close,
}: {
  show: boolean;
  completeAuth: () => void;
  close: () => void;
}) => {
  const { authenticate } = useAuthenticate();
  const [otpCode, setOtpCode] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(false);

  // Authenticate a user using a bundle returned from a deep link
  const handleUserOtp = useCallback(
    (otpCode: string) => {
      try {
        setLoading(true);
        authenticate({ otpCode, type: "otp" }); //<-- Pass the user's OTP code to the authenticate method using `otp` as the type value

        completeAuth();
      } catch (e) {
        Alert.alert("Error verifying OTP Code. Check logs for more details.");

        console.log("Error verifying OTP CODE: ", e);
      }
    },
    [authenticate]
  );

  return (
    <Modal visible={show} style={{ paddingTop: 200 }}>
      <SafeAreaView style={{ margin: 20 }}>
        <Text style={{ fontSize: 16 }}>Enter OTP</Text>
        <TextInput
          style={{ fontSize: 20 }}
          value={otpCode}
          onChangeText={setOtpCode}
          placeholder="Enter OTP"
        />
        <Button
          title={loading ? "Loading" : "Submit OTP"}
          disabled={loading}
          onPress={() => handleUserOtp(otpCode)}
        />
        <Button
          title="Close"
          onPress={() => {
            setLoading(false);
            close();
          }}
        />
      </SafeAreaView>
    </Modal>
  );
};
```

