// software distributed under the License is distributed on an
// "AS IS" BASIS, WITHOUT WARRANTIES OR CONDITIONS OF ANY
// KIND, either express or implied.  See the License for the
// specific language governing permissions and limitations
// under the License.

#include <arrow/api.h>
#include <arrow/compute/api.h>
#include <arrow/compute/api_scalar.h>
#include <arrow/io/api.h>
#include <arrow/result.h>
#include <arrow/status.h>
#include <arrow/table.h>

#include <cassert>
#include <cstdlib>
#include <iostream>

// Demonstrate instruction on the Arrow C++ compute functions API
//
// Create arrow table with a numerical column and compute
// min, max mean values.
//
// Writes "compute_example demo completed successfully\n" to stdout when complete.

#include "arrow/util/logging.h"

#include <fmt/core.h>

arrow::Status RunMain() {
  arrow::Int64Builder a_builder;
  arrow::Int64Builder b_builder;

  ARROW_RETURN_NOT_OK(a_builder.AppendValues({1, 2, 2223, 3}));
  ARROW_RETURN_NOT_OK(b_builder.AppendValues({3, 5, 7, 11}));

  std::shared_ptr<arrow::Array> a, b;
  ARROW_ASSIGN_OR_RAISE(a, a_builder.Finish());
  ARROW_ASSIGN_OR_RAISE(b, b_builder.Finish());

  std::shared_ptr<arrow::Field> field_a, field_b;
  field_a = arrow::field("A", arrow::int64());
  field_b = arrow::field("B", arrow::int64());

  std::shared_ptr<arrow::Schema> schema;
  schema = arrow::schema({field_a, field_b});

  std::shared_ptr<arrow::Table> table;
  table = arrow::Table::Make(schema, {a, b});

  fmt::print("Table size: {}\n", table->num_rows());
  fmt::print("-- Function with convenience function --\n");

  ARROW_ASSIGN_OR_RAISE(arrow::Datum mean_a, arrow::compute::Mean(table->column(0)));
  fmt::print("Mean of column a: {}\n", mean_a.scalar_as<arrow::DoubleScalar>().value);

  fmt::print("-- Function with direct call --\n");

  ARROW_ASSIGN_OR_RAISE(
      arrow::compute::Datum sum_b,
      arrow::compute::CallFunction("sum", {arrow::compute::Datum(table->column(1))}));
  fmt::print("Sum of column b: {}\n", sum_b.scalar_as<arrow::Int64Scalar>().value);

  fmt::print("-- Function needing options struct --\n");

  arrow::compute::IndexOptions options;
  options.value = arrow::compute::Datum(2223);

  ARROW_ASSIGN_OR_RAISE(arrow::compute::Datum result,
                         arrow::compute::CallFunction("index", {table->column(0)}, &options));

  ARROW_ASSIGN_OR_RAISE(auto scalar_result, result.scalar());
  fmt::print("index result: {}\n", scalar_result->ToString());

  fmt::print("compute_example demo completed successfully\n");
  return arrow::Status::OK();
}

int main() {
  arrow::Status st = RunMain();
  if (!st.ok()) {
    std::cerr << st << std::endl;
    return EXIT_FAILURE;
  }
  return EXIT_SUCCESS;
}
```

