  } & { " $fragmentRefs"?: { UserFragment: UserFragment } }
}

type UserFragment = {
  __typename: "User";
  age: number | null;
} & { " $fragmentName"?: "UserFragment" }

type UnmaskedQueryType = Unmasked<QueryType>;
