```
import { visit } from "graphql/language/visitor";

function flattenSelections(selection: SelectionNode) {
  const selections: SelectionNode[] = [];
  visit(selection, {
    SelectionSet(ss) {
      selections.push(...ss.selections);
    },
  });
  return selections;
}
```

