import { Controller, Get, Req, Res, HttpStatus } from "@nestjs/common";
import { Request, Response } from "express";
import { ArcjetClient } from "@arcjet/node";

@Controller()
export class AppController {
  constructor(private readonly arcjet: ArcjetClient) {}

  @Get()
  async getHello(@Req() req: Request, @Res() res: Response) {
    const result = await this.arcjet.protect({
      request: req,
      rules: {
        rateLimit: { tokensPerInterval: 10, interval: "1 minute" },
      },
    });

    if (result.decision === "DENY") {
      return res
        .status(HttpStatus.TOO_MANY_REQUESTS)
        .json({ error: "Too many requests" });
    }

    return res.json({ message: "Hello World!" });
  }
}
```

