```
import { OpenAIStream, StreamingTextResponse } from "ai";
import { Configuration, OpenAIApi } from "openai-edge";
import { Ratelimit } from "@upstash/ratelimit";
import { Redis } from "@upstash/redis";
import { useArcjet } from "@arcjet/next";
import { getTokenEstimate } from "openai-chat-tokens";

// Configure OpenAI
const config = new Configuration({
  apiKey: process.env.OPENAI_API_KEY,
});
const openai = new OpenAIApi(config);

// Create an Arcjet rule that uses userId as the rate limit key
const aj = useArcjet({
  rules: [
    {
      characteristics: [{ name: "userId", value: "$.session.userId" }],
      budget: 2000,
      window: "1h",
      bucketSize: 5000,
    },
    { mode: "block_bot" },
  ],
});

// POST request handler for the chat API route
export async function POST(req: Request) {
  const json = await req.json();
  const { messages } = json;
  const userId = "user_123"; // Get this from your auth system

  // Estimate the number of tokens that will be used
  const tokenEstimate = getTokenEstimate(messages);

  // Check rate limit and bot rules with Arcjet
  try {
    await aj.protect(req, {
      characteristics: { userId },
      tokens: tokenEstimate,
    });
  } catch (err) {
    return new Response("Rate limit exceeded", { status: 429 });
  }

  // Call OpenAI chat API
  const response = await openai.createChatCompletion({
    model: "gpt-3.5-turbo",
    stream: true,
    messages,
  });

  // Create a streaming response
  const stream = OpenAIStream(response);
  return new StreamingTextResponse(stream);
}
