import { Arcjet } from "@arcjet/next";
import { ArcjetResponse } from "@/libs/ArcjetResponse";

const aj = new Arcjet({
  key: process.env.ARCJET_KEY!,
  rules: [
    {
      // type of rule: rate limit
      name: "rate_limit",
      // type of algorithm
      type: "sliding_window",
      // maximum number of requests
      max: 100,
      // window size in seconds
      window: 60,
      // what to key the rate limit by
      key: "ip",
      // what to match on
      match: {
        url: "/api/hello",
      },
    },
  ],
});

export async function GET(req: Request) {
  const decision = await aj.protect(req);

  if (decision.isDenied()) {
    return ArcjetResponse.rateLimit(decision);
  }

  return new Response("Hello, World!");
}
