import { Controller, Get, UseGuards } from "@nestjs/common";
import { ArcjetGuard } from "./arcjet.guard";
import { ArcjetRules } from "@arcjet/guard";

@Controller()
export class AppController {
  @Get()
  @UseGuards(ArcjetGuard)
