<SelectableContent client:load syncKey="language" frameworkSwitcher>
  <div slot="TS" slotIdx="1">
    <Code
      code={Step3TS}
      lang="ts"
      title="app/routes/arcjet.tsx"
      mark={["ARCJET_KEY"]}
    />
  </div>
  <div slot="JS" slotIdx="2">
    <Code
      code={Step3JS}
      lang="js"
      title="app/routes/arcjet.jsx"
