import { ArrowDownIcon, ArrowUpIcon } from "lucide-react";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

export function PriceSnapshot({
  ticker,
  price,
  day_change,
  day_change_percent,
  time,
