TITLE: Creating Runtime Provider Component
DESCRIPTION: Client-side runtime provider component that integrates Vercel AI SDK's chat functionality with Assistant UI.
SOURCE: https://github.com/assistant-ui/assistant-ui/blob/main/apps/docs/content/docs/runtimes/langserve.mdx#2025-04-21_snippet_3

LANGUAGE: tsx
CODE:
```
"use client";

import { useChat } from "ai/react";
import { AssistantRuntimeProvider } from "@assistant-ui/react";
import { useVercelUseChatRuntime } from "@assistant-ui/react-ai-sdk";

export function MyRuntimeProvider({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  const chat = useChat({
    api: "/api/chat",
    unstable_AISDKInterop: true,
  });

  const runtime = useVercelUseChatRuntime(chat);

  return (
