TITLE: Implementing Cross-Chain Calculator Sender Contract in Solidity
DESCRIPTION: A Solidity contract that implements the sender part of a cross-chain calculator. It uses the Teleporter protocol to send two numbers from C-Chain to Avalanche L1 for calculation. The contract includes functionality to encode parameters and send cross-chain messages.
SOURCE: https://github.com/ava-labs/builders-hub.git/blob/master/content/academy/interchain-messaging/06-invoking-functions/03-create-simple-calculator-sender.mdx#2025-04-23_snippet_0

LANGUAGE: solidity
CODE:
```
pragma solidity ^0.8.18;

import "@teleporter/ITeleporterMessenger.sol";

contract SimpleCalculatorSenderOnCChain {
    ITeleporterMessenger public immutable teleporterMessenger =
        ITeleporterMessenger(0x253b2784c75e510dD0fF1da844684a1aC0aa5fcf);

    function sendAddMessage(address destinationAddress, uint256 num1, uint256 num2) external {
        teleporterMessenger.sendCrossChainMessage(
            TeleporterMessageInput({
                destinationBlockchainID: 0xd7ed7b978d4d6c478123bf9b326d47e69f959206d34e42ea4de2d1d2acbc93ea,
                destinationAddress: destinationAddress,
                feeInfo: TeleporterFeeInfo({feeTokenAddress: address(0), amount: 0}),
                requiredGasLimit: 100000,
                allowedRelayerAddresses: new address[](0),
                message: encodeAddData(num1, num2)
            })
        );
    }

    function encodeAddData(uint256 a, uint256 b) public pure returns (bytes memory) {
        bytes memory paramsData = abi.encode(a, b);
        return paramsData;
    }
}
```

