TITLE: Implementing SimpleCalculatorReceiverOnSubnet Contract in Solidity
DESCRIPTION: This Solidity contract implements a simple calculator receiver that can accept two numbers via cross-chain messaging, add them, and store the result. It uses the Teleporter protocol for interchain communication.
SOURCE: https://github.com/ava-labs/builders-hub.git/blob/master/content/academy/interchain-messaging/06-invoking-functions/04-create-simple-calulcator-receiver.mdx#2025-04-23_snippet_0

LANGUAGE: solidity
CODE:
```
pragma solidity ^0.8.18;

import "@teleporter/ITeleporterMessenger.sol";
import "@teleporter/ITeleporterReceiver.sol";

contract SimpleCalculatorReceiverOnSubnet is ITeleporterReceiver {
    ITeleporterMessenger public immutable teleporterMessenger =
        ITeleporterMessenger(0x253b2784c75e510dD0fF1da844684a1aC0aa5fcf);

    uint256 public result_num;

    function receiveTeleporterMessage(bytes32, address, bytes calldata message) external {
        // Only the Interchain Messaging receiver can deliver a message.
        require(
            msg.sender == address(teleporterMessenger), "CalculatorReceiverOnSubnet: unauthorized TeleporterMessenger"
        );

        (uint256 a, uint256 b) = abi.decode(message, (uint256, uint256));
        _calculatorAdd(a, b);
    }

    function _calculatorAdd(uint256 _num1, uint256 _num2) internal {
        result_num = _num1 + _num2;
    }
}
```

