class Resource {
  static #loaderInstance = null;

  static get #loader() {
    if (!this.#loaderInstance) this.#loaderInstance = new Loader();
    return this.#loaderInstance;
  }

  status = null;

  constructor(url) {
    this.status = Resource.#loader.load(url);
  }
}
```

